<?phpif (!defined('_PS_VERSION_'))	exit;	class mpsellerlistviewmorelistModuleFrontController extends ModuleFrontController	{		public function initContent() {			global $smarty;				global $cookie;			parent::initContent();							$link = new Link();						$obj_seller = new SellerInfoDetail();						if(isset($_GET['alp'])) {				$smarty->assign('alph',$_GET['alp']);				$all_active_seller = $obj_seller->findAllActiveSellerInfoByLimit(false,false,true,false,$_GET['alp']);			} else if(isset($_POST['submit_search_seller'])){				$all_active_seller = $obj_seller->findAllActiveSellerInfoByLimit(false,false,true,true,$_POST['search_seller_block_input']);				$smarty->assign('alph','0');			} else {				$all_active_seller = $obj_seller->findAllActiveSellerInfoByLimit();				$smarty->assign('alph','0');			}			//var_dump($all_active_seller);			//$all_active_seller = $obj_seller->findAllActiveSellerInfoByLimit();			if($all_active_seller) {				$total_active_seller = count($all_active_seller);				$param = array('flag'=>1);				$shop_store_link = $link->getModuleLink('marketplace','shop_store',$param);				foreach($all_active_seller as $act_seller) {					$img_file = 'modules/marketplace/img/shop_img/'.$act_seller['id'].'-'.$act_seller['shop_name'].'.jpg';										if(file_exists($img_file)) {						$shop_img[] = $act_seller['id'].'-'.$act_seller['shop_name'].'.jpg';					} else {						$shop_img[] = 'defaultshopimage.jpg';					}				}														$smarty->assign('shop_img',$shop_img);				$smarty->assign('shop_store_link',$shop_store_link);				$smarty->assign('all_active_seller',$all_active_seller);			} else {				$total_active_seller = 0;			}			$param = array('flag'=>1);				$viewmorelist_link = $link->getModuleLink('mpsellerlist','viewmorelist',$param);			$smarty->assign('viewmorelist_link',$viewmorelist_link);			$smarty->assign('total_active_seller',$total_active_seller);			$this->setTemplate('mpallsellerlist.tpl');		}		public function setMedia() {			parent::setMedia();			$this->addCSS(_MODULE_DIR_.'mpsellerlist/views/css/sellerlist.css');		}	}?>